package main

import "fmt"

func main() {
	s := "abc"
	t := "ahbgdc"
	s2 := "axc"
	t2 := "ahbgdc"
	s3 := "twn"
	t3 := "xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxtxxxxxxxxxxxxxxxxxxxxwxxxxxxxxxxxxxxxxxxxxxxxxxn"

	s4 := "leeeeetcode"
	t4 := ""

	fmt.Println(isSubsequence(s, t))
	fmt.Println(isSubsequence(s2, t2))
	fmt.Println(isSubsequence(s3, t3))
	fmt.Println(isSubsequence(s4, t4))
}

func isSubsequence(s string, t string) bool {
	if s == "" {
		return true
	}
	x := []byte(t)
	for i := 0; i < len(s); {
		if len(x) < len(s) {
			return false
		}
		if s[i] != x[i] {
			x = append(x[:i], x[i+1:]...)
		} else {
			i++
		}
	}
	// cut the tail
	x = x[:len(s)]
	if string(x) == s {
		return true
	}
	return false
}
